// This simulates receiving a post - in production, could be from a form, email, etc.
// For testing, just edit the post content here:

const originalPost = `Just finished Day 1 of my automation challenge! Built an email classifier that saves 2 hours daily. It reads emails, identifies urgency, and even drafts responses. This is why I love n8n!`;

const topic = "automation"; // technology, marketing, business, etc.

return {
  originalPost: originalPost,
  topic: topic,
  timestamp: new Date().toISOString(),
  postLength: originalPost.length
};

// Debug version - let's see what we're getting
const input = $input.all();
console.log("Raw input:", JSON.stringify(input));

// Try to get the data
let linkedinData;
try {
  // Check different possible structures
  if (input[0]?.json) {
    const response = input[0].json;
    if (response[0]?.message?.content) {
      linkedinData = response[0].message.content;
    } else if (response.message?.content) {
      linkedinData = response.message.content;
    } else if (response.content) {
      linkedinData = response.content;
    }
  }

  if (!linkedinData) {
    throw new Error("Could not find LinkedIn data");
  }
} catch (error) {
  return {
    platform: "LinkedIn",
    error: error.message,
    debug: JSON.stringify(input)
  };
}

// Format hashtags
const hashtags = linkedinData.hashtags.map(tag => `#${tag}`).join(' ');

// Create final post
const finalPost = `${linkedinData.content}\n\n${hashtags}`;

// Create preview
