// Debug version for Twitter
const input = $input.all();

// Try to get the data
let twitterData;
try {
  // Check different possible structures
  if (input[0]?.json) {
    const response = input[0].json;
    if (response[0]?.message?.content) {
      twitterData = response[0].message.content;
    } else if (response.message?.content) {
      twitterData = response.message.content;
    } else if (response.content) {
      twitterData = response.content;
    }
  }

  if (!twitterData) {
    throw new Error("Could not find Twitter data");
  }
} catch (error) {
  return {
    platform: "Twitter",
    error: error.message,
    debug: JSON.stringify(input)
  };
}

// Get tweets and hashtags
const tweets = twitterData.tweets || [{content: "Error: No tweets", position: 1}];
const hashtags = twitterData.hashtags ? twitterData.hashtags.map(tag => `#${tag}`).join(' ') : '';

// Format tweets with hashtags on the last one
const formattedTweets = tweets.map((tweet, index) => {
  const isLast = index === tweets.length - 1;
  const content = isLast ? `${tweet.content} ${hashtags}` : tweet.content;

  return {
    position: tweet.position,
    content: content,
    characterCount: content.length
  };
});

// Create preview
let preview = `
ðŸ¦ TWITTER/X PREVIEW:
â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
`;
formattedTweets.forEach(tweet => {
  preview += `[${tweet.position}/${tweets.length}] ${tweet.content}\n\n`;
});
preview += `â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”`;

return {
  platform: "Twitter",
  tweets: formattedTweets,
  preview: preview,
  finalPost: formattedTweets.map(t => t.content).join('\n\n'),
  isThread: tweets.length > 1
};

